mapper.select:
SELECT A.seq AS post_no, A.title, A.content,
CASE WHEN EXISTS ( SELECT 1 FROM B_SCRAP C WHERE C.post_no = A.seq AND C.user_no = #{user_no} ) THEN 'Y' ELSE 'N' END AS scrap_yn, ( SELECT COUNT(*) FROM B_SCRAP C WHERE C.post_no = A.seq ) AS scrap_num,
CASE WHEN EXISTS ( SELECT 1 FROM B_POST_LIKE B WHERE B.post_no = A.seq AND B.user_no = #{user_no} ) THEN 'Y' ELSE 'N' END AS like_yn, ( SELECT COUNT(*) FROM B_POST_LIKE B WHERE B.post_no = A.seq ) AS like_num, DATE_FORMAT(A.created_at, '%Y/%m/%d %H:%i:%s') AS created_at, DATE_FORMAT(A.last_modified_at, '%Y/%m/%d %H:%i:%s') AS last_modified_at FROM B_POST A INNER JOIN B_SCRAP B ON A.seq = B.post_no WHERE B.user_no = #{user_no} ORDER BY A.created_at DESC;
