mapper.select:
SELECT CAST(A.seq AS CHAR) AS post_no, A.title, A.content, M_USER.id AS post_user_id,
A.url, CASE WHEN EXISTS ( SELECT 1 FROM B_SCRAP C WHERE C.post_no = A.seq AND C.user_no = #{userId} ) THEN 'Y' ELSE 'N' END AS scrap_yn, CAST(( SELECT COUNT(*) FROM B_SCRAP C WHERE C.post_no = A.seq ) AS CHAR) AS scrap_num,
CASE WHEN EXISTS ( SELECT 1 FROM B_POST_LIKE B WHERE B.post_no = A.seq AND B.user_no = #{userId} ) THEN 'Y' ELSE 'N' END AS like_yn, CAST(( SELECT COUNT(*) FROM B_POST_LIKE B WHERE B.post_no = A.seq ) AS CHAR) AS like_num, CASE WHEN EXISTS ( SELECT 1 FROM B_COMMENT BC WHERE BC.post_no = A.seq AND BC.user_no = #{userId} ) THEN 'Y' ELSE 'N' END AS comment_yn, CAST(( SELECT COUNT(*) FROM B_COMMENT BC WHERE BC.post_no = A.seq ) AS CHAR) AS comment_num, DATE_FORMAT(A.created_at, '%Y/%m/%d %H:%i:%s') AS created_at, DATE_FORMAT(A.last_modified_at, '%Y/%m/%d %H:%i:%s') AS last_modified_at FROM B_POST A INNER JOIN M_USER ON A.user_no = M_USER.seq WHERE A.board_type = #{boardType} and A.state = 'Y' ORDER BY A.created_at DESC;

SELECT A.title, A.content,
A.url, CASE WHEN EXISTS ( SELECT 1 FROM B_SCRAP C WHERE C.post_no = #{boardId} AND C.user_no = #{userId} ) THEN 'Y' ELSE 'N' END AS scrap_yn, CAST(( SELECT COUNT(*) FROM B_SCRAP C WHERE C.post_no = #{boardId} ) AS CHAR) AS scrap_num,
CASE WHEN EXISTS ( SELECT 1 FROM B_POST_LIKE B WHERE B.post_no = #{boardId} AND B.user_no = #{userId} ) THEN 'Y' ELSE 'N' END AS like_yn, CAST(( SELECT COUNT(*) FROM B_POST_LIKE B WHERE B.post_no = #{boardId} ) AS CHAR) AS like_num, CASE WHEN EXISTS ( SELECT 1 FROM B_COMMENT BC WHERE BC.post_no = A.seq AND BC.user_no = #{userId} ) THEN 'Y' ELSE 'N' END AS comment_yn, CAST(( SELECT COUNT(*) FROM B_COMMENT BC WHERE BC.post_no = A.seq ) AS CHAR) AS comment_num, DATE_FORMAT(A.created_at, '%Y/%m/%d %H:%i:%s') AS created_at, DATE_FORMAT(A.last_modified_at, '%Y/%m/%d %H:%i:%s') AS last_modified_at FROM B_POST A WHERE A.seq = #{boardId} AND A.board_type = #{boardType}

mapper.delete:
DELETE
FROM
    B_POST_LIKE
WHERE
    post_no = #{boardId}
    AND user_no = #{userId}

mapper.insert:
 INSERT INTO
     B_POST_LIKE (
     post_no,
     user_no
 )
 VALUES (
         #{boardId},
         #{userId}
)

delete.if:
DELETE
FROM
    B_COMMENT_LIKE
WHERE
    comment_no = #{commentId}
    AND user_no = #{userId}

DELETE
FROM
    B_COMMENT_HATE
WHERE
    comment_no = #{commentId}
    AND user_no = #{userId}

insert.if:
INSERT INTO
    B_COMMENT_LIKE (
    comment_no,
    user_no
)
VALUES (
    #{commentId},
    #{userId}
)

INSERT INTO
    B_COMMENT_HATE (
    comment_no,
    user_no
)
VALUES (
    #{commentId},
    #{userId}
)

mapper.delete:
DELETE
FROM
    B_SCRAP
WHERE
    post_no = #{boardId}
  AND user_no = #{userId}

mapper.insert:
INSERT INTO
    B_SCRAP (
    post_no,
    user_no
)
VALUES (
           #{boardId},
           #{userId}
       )
