mapper.select:
SELECT CAST(A.seq AS CHAR) AS post_no, A.title, A.content,
A.url, CASE WHEN EXISTS ( SELECT 1 FROM B_SCRAP C WHERE C.post_no = A.seq ) THEN 'Y' ELSE 'N' END AS scrap_yn, CAST(( SELECT COUNT(*) FROM B_SCRAP C WHERE C.post_no = A.seq ) AS CHAR) AS scrap_num,
CASE WHEN EXISTS ( SELECT 1 FROM B_POST_LIKE B WHERE B.post_no = A.seq ) THEN 'Y' ELSE 'N' END AS like_yn, CAST(( SELECT COUNT(*) FROM B_POST_LIKE B WHERE B.post_no = A.seq ) AS CHAR) AS like_num, DATE_FORMAT(A.created_at, '%Y/%m/%d %H:%i:%s') AS created_at, DATE_FORMAT(A.last_modified_at, '%Y/%m/%d %H:%i:%s') AS last_modified_at FROM B_POST A WHERE A.board_type = #{board_type} and A.state = 'Y' ORDER BY A.created_at DESC;

SELECT A.title, A.content,
A.url, CASE WHEN EXISTS ( SELECT 1 FROM B_SCRAP C WHERE C.post_no = #{boardId} ) THEN 'Y' ELSE 'N' END AS scrap_yn, CAST(( SELECT COUNT(*) FROM B_SCRAP C WHERE C.post_no = #{boardId} ) AS CHAR) AS scrap_num,
CASE WHEN EXISTS ( SELECT 1 FROM B_POST_LIKE B WHERE B.post_no = #{boardId} ) THEN 'Y' ELSE 'N' END AS like_yn, CAST(( SELECT COUNT(*) FROM B_POST_LIKE B WHERE B.post_no = #{boardId} ) AS CHAR) AS like_num, DATE_FORMAT(A.created_at, '%Y/%m/%d %H:%i:%s') AS created_at, DATE_FORMAT(A.last_modified_at, '%Y/%m/%d %H:%i:%s') AS last_modified_at FROM B_POST A WHERE A.seq = #{boardId}
